import React,{Component} from 'react'
import jwt_decode from 'jwt-decode'
class profile extends Component {
    constructor(){
        super();
        this.state = {
            title: '',
            description: '',
            tareas: []

        };
        this.addTarea = this.addTarea.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    addTarea(e){
        fetch('http://localhost:3000//api/tareas',{
            method: 'POST',
            body: JSON.stringify(this.state),
            headers:{
                'Accept' : 'application/json',
                'Content-Type':'application/json'
            }
        })
        .then(res=>console.log(res))
        .then(data=>{
            //console.log(data)
            //M.toast({html:'tarea guardada'});
            this.setState({title: '',description: ''});
            this.fetchTareas();
        })
        .catch(err => console.error(err));
        e.preventDefault();
    }
handleChange(e){
    const {name,value} = e.target;
    this.setState({
        [name]:value
    });
}

fetchTareas(){

    fetch('http://localhost:3000/api/tareas')
    .then(res =>res.json())
    .then(data =>{
        this.setState({tareas: data});
        console.log(this.state.tareas);
    });
}
componentDidMount(){
    this.fetchTareas();
}
    render() {
        return ( 
        <div> 
        < nav className = "ligth-blue darken-3" > 
        < div className = "container" > 
        < a className = "brand-logo"href = "/" > Proyecto 1 
        </a>
        </div>
        </nav> 
        <div className="container">
            <div className ="row">
                <div className= "col s5">
                    <div className="card">
                        <div className= "card-content">
                         <form onSubmit={this.addTarea}>
                             <div className= "row">
                                <div className= "input-field col s12">
                                    <input name="title" onChange={this.handleChange} type="text" placeholder="Nombre del usuario" value={this.state.title}/>
                                </div>
                            </div>
                            <div className= "row">
                                 <div className= "input-field col s12">
                                        <textarea name="description" onChange={this.handleChange} placeholder="registro de datos" className ="materialize-textarea" value={this.state.description}> </textarea>
                                </div>
                            </div>
                            <button type="submit"className="btn btn-light-yellow darken-2">
                                enviar
                            </button>    
                            </form>
                        </div>
                    
                    </div>
                </div>
                <div className= "col s7">
                    <table>
                        <thead>
                            <tr>
                                <th>Usuario</th>
                                <th>Registro de datos</th>
                            </tr>   
                         </thead>  
                         <tbody>
                        {this.state.tareas.map(tarea => {
                            return (
                                <tr key={tarea._id}>
                        <td>{tarea.title}</td>
                        <td>{tarea.description}</td>
                                </tr>
                            )
                        })}
                         </tbody> 
                    </table>
                </div>
            </div>
        </div>
    </div> 
        )
    }
}
    export default profile;
